//js 读取文件
function read(files){
    if(files.length){
        var file = files[0]
        var reader = new FileReader()
        if(/text/.test(file.type)){ //判断文件类型
            reader.onload - function(){
                $("show").append('<pre>'+this.result+'</pre>')
            }
            reader.readAsText(file)
        }else{
            $("show").append('<pre>格式错误</pre>')
        }
    }
}

